function formatCPF(e){let l=e.value.replace(/\D/g,"");l.length>11&&(l=l.slice(0,11)),l=(l=(l=l.replace(/(\d{3})(\d)/,"$1.$2")).replace(/(\d{3})(\d)/,"$1.$2")).replace(/(\d{3})(\d{1,2})$/,"$1-$2"),e.value=l}function formatCEP(e){let l=e.value.replace(/\D/g,"");l.length>8&&(l=l.slice(0,8)),l=l.replace(/(\d{5})(\d)/,"$1-$2"),e.value=l}
